from ga import initialize, regular_ga, darwin_ga, lamarck_ga, plot# --------- prints a menu for program for the to console -----------------------------#running = Truewhile running:    print("Please enter the full filename for board configuration (e.g 'config.txt'): ")    filename = input()    try:        initialize(filename)        print("Running Regular GA...")        regular_ga()        print("Running Darwin GA...")        darwin_ga()        print("Running Lamarck GA...")        lamarck_ga()                plot()                print("Start over? (Y/N)")        startover = input()        if startover.lower() == 'n':            running = False    except:        print("Something's wrong. please try again")            # --------------------------------------------------------------------------------#